#!/usr/bin/envpython3"""DesignTokenGeneratorCreatesconsistentdesignsystemtokensforcolors,typography,spacing,andmore"""importjsonfromtypingimportDict,List,TupleimportcolorsysclassDesignTokenGenerator:"""Generatecomprehensivedesignsystemtokens"""def__init__(self):self.base_unit=8#8ptgridsystemself.type_scale_ratio=1.25#Majorthirdself.base_font_size=16defgenerate_complete_system(self,brand_color:str="#0066CC",style:str="modern")->Dict:"""Generatecompletedesigntokensystem"""tokens={'meta':{'version':'1.0.0','style':style,'generated':'auto-generated'},'colors':self.generate_color_palette(brand_color),'typography':self.generate_typography_system(style),'spacing':self.generate_spacing_system(),'sizing':self.generate_sizing_tokens(),'borders':self.generate_border_tokens(style),'shadows':self.generate_shadow_tokens(style),'animation':self.generate_animation_tokens(),'breakpoints':self.generate_breakpoints(),'z-index':self.generate_z_index_scale()}returntokensdefgenerate_color_palette(self,brand_color:str)->Dict:"""Generatecomprehensivecolorpalettefrombrandcolor"""#ConverthextoRGBbrand_rgb=self._hex_to_rgb(brand_color)brand_hsv=colorsys.rgb_to_hsv(*[c/255forcinbrand_rgb])palette={'primary':self._generate_color_scale(brand_color,'primary'),'secondary':self._generate_color_scale(self._adjust_hue(brand_color,180),'secondary'),'neutral':self._generate_neutral_scale(),'semantic':{'success':{'base':'#10B981','light':'#34D399','dark':'#059669','contrast':'#FFFFFF'},'warning':{'base':'#F59E0B','light':'#FBBD24','dark':'#D97706','contrast':'#FFFFFF'},'error':{'base':'#EF4444','light':'#F87171','dark':'#DC2626','contrast':'#FFFFFF'},'info':{'base':'#3B82F6','light':'#60A5FA','dark':'#2563EB','contrast':'#FFFFFF'}},'surface':{'background':'#FFFFFF','foreground':'#111827','card':'#FFFFFF','overlay':'rgba(0,0,0,0.5)','divider':'#E5E7EB'}}returnpalettedef_generate_color_scale(self,base_color:str,name:str)->Dict:"""Generatecolorscalefrombasecolor"""scale={}rgb=self._hex_to_rgb(base_color)h,s,v=colorsys.rgb_to_hsv(*[c/255forcinrgb])#Generatescalefrom50to900steps=[50,100,200,300,400,500,600,700,800,900]forstepinsteps:#Adjustlightnessbasedonstepfactor=(1000-step)/1000new_v=0.95ifstep<500elsev*(1-(step-500)/500)new_s=s*(0.3+0.7*(step/900))new_rgb=colorsys.hsv_to_rgb(h,new_s,new_v)scale[str(step)]=self._rgb_to_hex([int(c*255)forcinnew_rgb])scale['DEFAULT']=base_colorreturnscaledef_generate_neutral_scale(self)->Dict:"""Generateneutralcolorscale"""return{'50':'#F9FAFB','100':'#F3F4F6','200':'#E5E7EB','300':'#D1D5DB','400':'#9CA3AF','500':'#6B7280','600':'#4B5563','700':'#374151','800':'#1F2937','900':'#111827','DEFAULT':'#6B7280'}defgenerate_typography_system(self,style:str)->Dict:"""Generatetypographysystem"""#Fontfamiliesbasedonstylefont_families={'modern':{'sans':'Inter,system-ui,-apple-system,sans-serif','serif':'Merriweather,Georgia,serif','mono':'FiraCode,Monaco,monospace'},'classic':{'sans':'Helvetica,Arial,sans-serif','serif':'TimesNewRoman,Times,serif','mono':'CourierNew,monospace'},'playful':{'sans':'Poppins,Roboto,sans-serif','serif':'PlayfairDisplay,Georgia,serif','mono':'SourceCodePro,monospace'}}typography={'fontFamily':font_families.get(style,font_families['modern']),'fontSize':self._generate_type_scale(),'fontWeight':{'thin':100,'light':300,'normal':400,'medium':500,'semibold':600,'bold':700,'extrabold':800,'black':900},'lineHeight':{'none':1,'tight':1.25,'snug':1.375,'normal':1.5,'relaxed':1.625,'loose':2},'letterSpacing':{'tighter':'-0.05em','tight':'-0.025em','normal':'0','wide':'0.025em','wider':'0.05em','widest':'0.1em'},'textStyles':self._generate_text_styles()}returntypographydef_generate_type_scale(self)->Dict:"""Generatemodulartypescale"""scale={}sizes=['xs','sm','base','lg','xl','2xl','3xl','4xl','5xl']fori,sizeinenumerate(sizes):ifsize=='base':scale[size]=f'{self.base_font_size}px'elifi<sizes.index('base'):factor=self.type_scale_ratio**(sizes.index('base')-i)scale[size]=f'{round(self.base_font_size/factor)}px'else:factor=self.type_scale_ratio**(i-sizes.index('base'))scale[size]=f'{round(self.base_font_size*factor)}px'returnscaledef_generate_text_styles(self)->Dict:"""Generatepre-composedtextstyles"""return{'h1':{'fontSize':'48px','fontWeight':700,'lineHeight':1.2,'letterSpacing':'-0.02em'},'h2':{'fontSize':'36px','fontWeight':700,'lineHeight':1.3,'letterSpacing':'-0.01em'},'h3':{'fontSize':'28px','fontWeight':600,'lineHeight':1.4,'letterSpacing':'0'},'h4':{'fontSize':'24px','fontWeight':600,'lineHeight':1.4,'letterSpacing':'0'},'h5':{'fontSize':'20px','fontWeight':600,'lineHeight':1.5,'letterSpacing':'0'},'h6':{'fontSize':'16px','fontWeight':600,'lineHeight':1.5,'letterSpacing':'0.01em'},'body':{'fontSize':'16px','fontWeight':400,'lineHeight':1.5,'letterSpacing':'0'},'small':{'fontSize':'14px','fontWeight':400,'lineHeight':1.5,'letterSpacing':'0'},'caption':{'fontSize':'12px','fontWeight':400,'lineHeight':1.5,'letterSpacing':'0.01em'}}defgenerate_spacing_system(self)->Dict:"""Generatespacingsystembasedon8ptgrid"""spacing={}multipliers=[0,0.5,1,1.5,2,2.5,3,4,5,6,7,8,9,10,12,14,16,20,24,32,40,48,56,64]fori,multinenumerate(multipliers):spacing[str(i)]=f'{int(self.base_unit*mult)}px'#Addsemanticspacingspacing.update({'xs':spacing['1'],#4px'sm':spacing['2'],#8px'md':spacing['4'],#16px'lg':spacing['6'],#24px'xl':spacing['8'],#32px'2xl':spacing['12'],#48px'3xl':spacing['16']#64px})returnspacingdefgenerate_sizing_tokens(self)->Dict:"""Generatesizingtokensforcomponents"""return{'container':{'sm':'640px','md':'768px','lg':'1024px','xl':'1280px','2xl':'1536px'},'components':{'button':{'sm':{'height':'32px','paddingX':'12px'},'md':{'height':'40px','paddingX':'16px'},'lg':{'height':'48px','paddingX':'20px'}},'input':{'sm':{'height':'32px','paddingX':'12px'},'md':{'height':'40px','paddingX':'16px'},'lg':{'height':'48px','paddingX':'20px'}},'icon':{'sm':'16px','md':'20px','lg':'24px','xl':'32px'}}}defgenerate_border_tokens(self,style:str)->Dict:"""Generatebordertokens"""radius_values={'modern':{'none':'0','sm':'4px','DEFAULT':'8px','md':'12px','lg':'16px','xl':'24px','full':'9999px'},'classic':{'none':'0','sm':'2px','DEFAULT':'4px','md':'6px','lg':'8px','xl':'12px','full':'9999px'},'playful':{'none':'0','sm':'8px','DEFAULT':'16px','md':'20px','lg':'24px','xl':'32px','full':'9999px'}}return{'radius':radius_values.get(style,radius_values['modern']),'width':{'none':'0','thin':'1px','DEFAULT':'1px','medium':'2px','thick':'4px'}}defgenerate_shadow_tokens(self,style:str)->Dict:"""Generateshadowtokens"""shadow_styles={'modern':{'none':'none','sm':'01px2px0rgba(0,0,0,0.05)','DEFAULT':'01px3px0rgba(0,0,0,0.1),01px2px0rgba(0,0,0,0.06)','md':'04px6px-1pxrgba(0,0,0,0.1),02px4px-1pxrgba(0,0,0,0.06)','lg':'010px15px-3pxrgba(0,0,0,0.1),04px6px-2pxrgba(0,0,0,0.05)','xl':'020px25px-5pxrgba(0,0,0,0.1),010px10px-5pxrgba(0,0,0,0.04)','2xl':'025px50px-12pxrgba(0,0,0,0.25)','inner':'inset02px4px0rgba(0,0,0,0.06)'},'classic':{'none':'none','sm':'01px2pxrgba(0,0,0,0.1)','DEFAULT':'02px4pxrgba(0,0,0,0.1)','md':'04px8pxrgba(0,0,0,0.1)','lg':'08px16pxrgba(0,0,0,0.1)','xl':'016px32pxrgba(0,0,0,0.1)'}}returnshadow_styles.get(style,shadow_styles['modern'])defgenerate_animation_tokens(self)->Dict:"""Generateanimationtokens"""return{'duration':{'instant':'0ms','fast':'150ms','DEFAULT':'250ms','slow':'350ms','slower':'500ms'},'easing':{'linear':'linear','ease':'ease','easeIn':'ease-in','easeOut':'ease-out','easeInOut':'ease-in-out','spring':'cubic-bezier(0.68,-0.55,0.265,1.55)'},'keyframes':{'fadeIn':{'from':{'opacity':0},'to':{'opacity':1}},'slideUp':{'from':{'transform':'translateY(10px)','opacity':0},'to':{'transform':'translateY(0)','opacity':1}},'scale':{'from':{'transform':'scale(0.95)'},'to':{'transform':'scale(1)'}}}}defgenerate_breakpoints(self)->Dict:"""Generateresponsivebreakpoints"""return{'xs':'480px','sm':'640px','md':'768px','lg':'1024px','xl':'1280px','2xl':'1536px'}defgenerate_z_index_scale(self)->Dict:"""Generatez-indexscale"""return{'hide':-1,'base':0,'dropdown':1000,'sticky':1020,'overlay':1030,'modal':1040,'popover':1050,'tooltip':1060,'notification':1070}defexport_tokens(self,tokens:Dict,format:str='json')->str:"""Exporttokensinvariousformats"""ifformat=='json':returnjson.dumps(tokens,indent=2)elifformat=='css':returnself._export_as_css(tokens)elifformat=='scss':returnself._export_as_scss(tokens)else:returnjson.dumps(tokens,indent=2)def_export_as_css(self,tokens:Dict)->str:"""ExportasCSSvariables"""css=[':root{']defflatten_dict(obj,prefix=''):forkey,valueinobj.items():ifisinstance(value,dict):flatten_dict(value,f'{prefix}-{key}'ifprefixelsekey)else:css.append(f'--{prefix}-{key}:{value};')flatten_dict(tokens)css.append('}')return'\n'.join(css)def_hex_to_rgb(self,hex_color:str)->Tuple[int,int,int]:"""ConverthextoRGB"""hex_color=hex_color.lstrip('#')returntuple(int(hex_color[i:i+2],16)foriin(0,2,4))def_rgb_to_hex(self,rgb:List[int])->str:"""ConvertRGBtohex"""return'#{:02x}{:02x}{:02x}'.format(*rgb)def_adjust_hue(self,hex_color:str,degrees:int)->str:"""Adjusthueofcolor"""rgb=self._hex_to_rgb(hex_color)h,s,v=colorsys.rgb_to_hsv(*[c/255forcinrgb])h=(h+degrees/360)%1new_rgb=colorsys.hsv_to_rgb(h,s,v)returnself._rgb_to_hex([int(c*255)forcinnew_rgb])defmain():importsysgenerator=DesignTokenGenerator()#Getparametersbrand_color=sys.argv[1]iflen(sys.argv)>1else"#0066CC"style=sys.argv[2]iflen(sys.argv)>2else"modern"output_format=sys.argv[3]iflen(sys.argv)>3else"json"#Generatetokenstokens=generator.generate_complete_system(brand_color,style)#Outputifoutput_format=='summary':print("="*60)print("DESIGNSYSTEMTOKENS")print("="*60)print(f"\nðŸŽ¨Style:{style}")print(f"ðŸŽ¨BrandColor:{brand_color}")print("\nðŸ“ŠGeneratedTokens:")print(f"â€¢Colors:{len(tokens['colors'])}palettes")print(f"â€¢Typography:{len(tokens['typography'])}categories")print(f"â€¢Spacing:{len(tokens['spacing'])}values")print(f"â€¢Shadows:{len(tokens['shadows'])}styles")print(f"â€¢Breakpoints:{len(tokens['breakpoints'])}sizes")print("\nðŸ’¾Exportformatsavailable:json,css,scss")else:print(generator.export_tokens(tokens,output_format))if__name__=="__main__":main()