fromplaywright.sync_apiimportsync_playwright#Example:Discoveringbuttonsandotherelementsonapagewithsync_playwright()asp:browser=p.chromium.launch(headless=True)page=browser.new_page()#Navigatetopageandwaitforittofullyloadpage.goto('http://localhost:5173')page.wait_for_load_state('networkidle')#Discoverallbuttonsonthepagebuttons=page.locator('button').all()print(f"Found{len(buttons)}buttons:")fori,buttoninenumerate(buttons):text=button.inner_text()ifbutton.is_visible()else"[hidden]"print(f"[{i}]{text}")#Discoverlinkslinks=page.locator('a[href]').all()print(f"\nFound{len(links)}links:")forlinkinlinks[:5]:#Showfirst5text=link.inner_text().strip()href=link.get_attribute('href')print(f"-{text}->{href}")#Discoverinputfieldsinputs=page.locator('input,textarea,select').all()print(f"\nFound{len(inputs)}inputfields:")forinput_elemininputs:name=input_elem.get_attribute('name')orinput_elem.get_attribute('id')or"[unnamed]"input_type=input_elem.get_attribute('type')or'text'print(f"-{name}({input_type})")#Takescreenshotforvisualreferencepage.screenshot(path='/tmp/page_discovery.png',full_page=True)print("\nScreenshotsavedto/tmp/page_discovery.png")browser.close()