#!/bin/bash#ScripttoconfigureautoscalingpoliciesforKubernetesdeployments#Checkifkubectlisinstalledif!command-vkubectl&>/dev/nullthenecho"kubectlnotfound.Pleaseinstallkubectltoproceed."exit1fi#DefinethedeploymentnameandnamespaceDEPLOYMENT_NAME="my-app"NAMESPACE="default"#ApplyHorizontalPodAutoscaler(HPA)echo"ApplyingHorizontalPodAutoscaler..."kubectlautoscaledeployment$DEPLOYMENT_NAME\--cpu-percent=50\--min=2\--max=10\--namespace=$NAMESPACEecho"HorizontalPodAutoscalerapplied."#ApplyClusterAutoscaler(ifusingacloudprovider)echo"ConfiguringClusterAutoscaler..."cat<<EOL|kubectlapply-f-apiVersion:autoscaling/v1kind:ClusterAutoscalermetadata:name:cluster-autoscalernamespace:kube-systemspec:scaleDown:enabled:truedelayAfterAdd:10mdelayAfterDelete:1mdelayAfterFailure:3mEOLecho"ClusterAutoscalerconfigured."echo"Autoscalingsetupcomplete."